package com.UEG.Justanothervitor.config;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.mongodb.core.MongoTemplate;

import com.UEG.Justanothervitor.domain.ProductDomain;
import com.UEG.Justanothervitor.domain.RolesDomain;
import com.UEG.Justanothervitor.domain.UserDomain;
import com.UEG.Justanothervitor.repository.ProductRepository;
import com.UEG.Justanothervitor.repository.RolesRepository;
import com.UEG.Justanothervitor.repository.UserRepository;

@Configuration
public class DatabaseInjection implements CommandLineRunner{
		
	@Autowired
	private MongoTemplate template;
		
	@Autowired 
	private UserRepository repoForUsers;
	
	@Autowired
	private RolesRepository roleRepo;
	
	@Autowired
	private ProductRepository repoForProducts;
	
	@Override
	public void run(String... args) throws Exception {
		
		template.dropCollection(UserDomain.class);
		template.dropCollection(ProductDomain.class);
		template.dropCollection(RolesDomain.class);
		
		/*UserDomain u0 = new UserDomain(null,"Felicio Nathan Dias","firstemail@gtreco.com","passwordtoyou");
		repoForUsers.createANewUser(u0);
*/		
		ProductDomain p0 = new ProductDomain(null,"Pasta de dente","Serve para escovar os dente","data:image/png;base64,",10.0);
		ProductDomain p1 = new ProductDomain(null,"Controle Xbox Series","CompatÃ­vel com todos os sistemas","data:image/jpeg;base64,",220.0);
		repoForProducts.createANewProduct(p0);
		repoForProducts.createANewProduct(p1);
		
	}
	
	@Bean
	CommandLineRunner init(RolesRepository roleRepository) {

	    return args -> {

	        RolesDomain adminRole = roleRepository.findByRole("ADMIN");
	        if (adminRole == null) {
	            RolesDomain newAdminRole = new RolesDomain();
	            newAdminRole.setRole("ADMIN");
	            roleRepository.save(newAdminRole);
	        }

	        RolesDomain userRole = roleRepository.findByRole("USER");
	        if (userRole == null) {
	            RolesDomain newUserRole = new RolesDomain();
	            newUserRole.setRole("CLIENT");
	            roleRepository.save(newUserRole);
	        }
	    };

	}

}
